export function getCategoryData(categoryId: string): CategoryData | null {
  return allCategoriesData.find(cat => cat.id === categoryId) || null;
}

export function getAllCategoryIds(): string[] {
  return allCategoriesData.map(cat => cat.id);
}

// Export count for verification
export const TOTAL_CATEGORIES = allCategoriesData.length;
export const TOTAL_PROMPTS = allCategoriesData.reduce((sum, cat) => sum + cat.promptTemplates.length, 0);
